mapper.insert:
INSERT INTO RESTAURANT (OWNER_SEQ,
                        CATEGORY,
                        OPEN_YN,
                        RESTAURANT_NAME,
                        INTRODUCTION,
                        MINIMUM_ORDER_AMOUNT,
                        ADDRESS_SEQ)
VALUES ( #{ownerSeq}
       , #{category}
       , #{openYN}
       , #{restaurantName}
       , #{introduction}
       , #{minimumOrderAmount}
       , #{addressSeq})

mapper.select:
SELECT RESTAURANT_SEQ
FROM RESTAURANT
WHERE OWNER_SEQ = #{ownerSeq}
  AND CATEGORY = #{category}
  AND OPEN_YN = #{openYN}
  AND RESTAURANT_NAME = #{restaurantName}
  AND INTRODUCTION = #{introduction}
  AND MINIMUM_ORDER_AMOUNT = #{minimumOrderAmount}
  AND ADDRESS_SEQ = #{addressSeq};
